export async function GET(request: Request) {
  const { searchParams } = new URL(request.url);
  const tabParam = searchParams.get("tab") as "new" | "final" | "migrated" | null;

  const tab = tabParam && tokenData[tabParam] ? tabParam : "new";

  return NextResponse.json(tokenData[tab]);
}
